import sympy as sp;text1='First text about surveillance and biopolitics';text2='Second text with more surveillance';text3='Third text focusing on biopolitical aspects';surveillance_total=sum((t.lower().count('surveillance') for t in [text1,text2,text3]));biopolitical_total=sum((t.lower().count('biopolitical') for t in [text1,text2,text3]));ratio=sp.Rational(surveillance_total,biopolitical_total) if biopolitical_total!=0 else 0;result=sp.N(ratio);print(result)