import sympy as sp; base_wage = sp.Rational(15); increase_pct = sp.Rational(7,100); employment_change = sp.Rational(2,100); expr = base_wage * (1 + increase_pct) * (1 + employment_change); result = expr; synthesis = f'Based on prior analysis, the minimum wage increase of {increase_pct*100}% leads to an estimated wage level of ${float(result):.2f}, with employment projected to grow by {employment_change*100}%.'; print(result)