import sympy as sp;task_durations=[sp.Rational(5),sp.Rational(3),sp.Rational(2),sp.Rational(4)];total_time=sum(task_durations);fallback_buffer=sp.Rational(1,10)*total_time;final_timeline=total_time+fallback_buffer;num_workers=4;resource_shares=[duration/total_time for duration in task_durations];resource_allocation=[share*num_workers for share in resource_shares];total_resources=sum(resource_allocation);result=total_resources;print(result)